pragma solidity ^0.5.12;

import "./oracle.sol";

contract call {
    address oracleAddress;

    uint strike;
    uint maturity;

    address payable debtor;
    address payable holder;

    modifier onlyDebtor(){
        require(msg.sender == debtor && block.number > maturity);
        _;
    }

    modifier onlyHolder() {
        require(msg.sender == holder && block.number <= maturity);
        _;
    }

    constructor(uint _strike, uint _maturity, address payable _holder, address _oracleAddress) public payable {
        //require colatteral for exchange
        require(msg.value > 2*strike && msg.value > 2*oracleVal());
        strike = _strike * (1 ether);
        maturity = _maturity;
        holder = _holder;
        oracleAddress = _oracleAddress;
        debtor = msg.sender;
    }

    function exercize() public onlyHolder{
        uint btceth = oracleVal() * (1 ether);
        if (btceth > strike){
            holder.transfer(btceth-strike);
            debtor.transfer(address(this).balance);
        }
    }

    function reclaim() public onlyDebtor{
        debtor.transfer(address(this).balance);
    }


    function balance() public view returns(uint){
        return address(this).balance;
    }

    function height() public view returns(uint){
        return block.number;
    }

    function oracleVal() public view returns(uint){
        oracle temp = oracle(oracleAddress);
        return temp.get();
    }
}
